#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

root="$PWD"

ffmpeg="$PWD/../../../../Sky/tools/ffmpeg"

input="$PWD/music/extra"

output="$PWD/music/extra"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

tempo()
{
    sh tempo.sh "$input/$1" "$output/$2" "$3" "$4"
}

#--------------------------------------------------------------------------------------------------
# Voice
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

tempo "indy.webm" "indy2.webm" 0.8 20
