#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

root="$PWD"

ffmpeg="$PWD/../../../../Sky/tools/ffmpeg"

input="$PWD/voice"

output="$PWD/../content/voice"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

volume()
{
    sh volume.sh "$input/$1" "$output/$1" "$2"
}

#--------------------------------------------------------------------------------------------------
# Voice
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

volume "kerner.wav"  4
volume "kerner2.wav" 4
volume "kerner3.wav" 4
