#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

upscale="$PWD/upscale"

temp="$upscale/temp"

#--------------------------------------------------------------------------------------------------
# Voice
#--------------------------------------------------------------------------------------------------

if [ "$1" = "restore" ]; then

    rm -f "$upscale"/*.mp4

    mv "$temp"/*.mp4 "$upscale"
else
    mv "$upscale"/*.mp4 "$temp"

    cp "attic.mp4"   "$upscale"
    cp "attic11.mp4" "$upscale"
    cp "attic12.mp4" "$upscale"
    cp "indy3.mp4"   "$upscale"
    cp "indy4.mp4"   "$upscale"
    cp "splash3.mp4" "$upscale"
fi
