#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

root="$PWD"

ffmpeg="$PWD/../../../../../Sky/tools/ffmpeg"

input="$PWD"

output="$PWD/upscale"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

topaz()
{
    sh topaz.sh "$input/$1" "$output/$1" $2
}

#--------------------------------------------------------------------------------------------------
# Voice
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

topaz "attic.mp4"   iris-3
topaz "attic11.mp4" iris-3
topaz "attic12.mp4" iris-3
topaz "indy3.mp4"   iris-3
topaz "indy4.mp4"   iris-3
topaz "splash3.mp4" iris-3
