#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

# BEGIN code
LivePortrait="$PWD/../../../../../Sky-runtime/bash/generate/LivePortrait"

input="$PWD/video/base"

output="$PWD/../content"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    if [ $# = 2 ]; then

        sh sync.sh "$input" "$1" "$output" $2
    else
        sh sync.sh "$input" "$1" "$output"
    fi
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$LivePortrait"
# END code

run "dialog"
run "dialog2"
run "dialog3"
