#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

volume="$PWD/../../../../../Sky/tools/ffmpeg/volume.sh"

input="$PWD/voice"

output="$PWD/../content/voice"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

apply()
{
    sh "$volume" "$input/$1" "$2" "$output/$1"
}

#--------------------------------------------------------------------------------------------------
# Voice
#--------------------------------------------------------------------------------------------------

apply "attic.mp3"  20
apply "attic2.mp3" 20
apply "attic3.mp3" 20

apply "cross.mp3"  20
apply "cross2.mp3" 20
apply "cross3.mp3" 20

apply "gargoyle.mp3"  20
apply "gargoyle2.mp3" 20
apply "gargoyle3.mp3" 20
