#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

ffmpeg="$PWD/../../../../../../Sky/tools/ffmpeg"

fix="$PWD/fix"

input="$PWD/.."

output="$PWD"

base="$PWD/base"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    if [ $# = 3 ]; then

        sh resize.sh "$fix/$1" "$2" "$output/$1" $3

    elif [ $# = 2 ]; then

        sh resize.sh "$fix/$1" "$input/$1" "$output/$1" $2
    else
        sh resize.sh "$fix/$1" "$input/$1" "$output/$1"
    fi
}

runBase()
{
    sh resize.sh "$fix/$1" "$base/$1" "$output/$1" $3 1
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

run "attic.mp4"    1
run "attic2.mp4"   1
run "indy.mp4"
run "indy2-v2.mp4" 1
run "indy4-v2.mp4" 1
run "indy5.mp4"    1
run "indy6.mp4"    1
run "book.mp4"     1
run "book2.mp4"    1
