#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

luma="$PWD/../../../../../Sky-runtime/bash/generate/luma"

input="$PWD"

output="$PWD/wide"

wide="$PWD/../data/wide"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    sh reframe-wide.sh "$wide/$1" "$input/$2" "$output/$2"
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$luma"

run "college.png" "college2.mp4"
