#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

Luma="$PWD/../../../../../Sky/tools/Luma"

input="$PWD"

output="$PWD/wide"

wide="$PWD/../data/wide"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    sh reframeWide.sh "$wide/$1" "$input/$2" "$output/$2"
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$Luma"

run "college.png" "college2.mp4"
