#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

LivePortrait="$PWD/../../../../../Sky/tools/LivePortrait"

input="$PWD/video/base"

output="$PWD/../content"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    if [ $# = 2 ]; then

        sh sync.sh "$1" "$input" "$output" $2
    else
        sh sync.sh "$1" "$input" "$output"
    fi
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$LivePortrait"

run "dialog"
run "dialog2"
run "dialog3" 0.5
run "dialog4"
run "dialog5"
