#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

Magnific="$PWD/../../../../../Sky/tools/Magnific"

input="$PWD/upscale/base"

output="$PWD/upscale"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    if [ $# = 3 ]; then

        sh upscale.sh "$input/$1.png" "$output/$1.png" $2 films_n_photography $3
    else
        sh upscale.sh "$input/$1.png" "$output/$1.png" $2 films_n_photography
    fi
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$Magnific"

run "college"   2x
run "college2"  2x
run "interior"  2x
run "interior2" 2x -2
run "interior3" 2x -2
run "interior4" 2x -2
run "interior5" 2x -2
