#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

upscale="$PWD/upscale"

temp="$upscale/temp"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    cp "$1" "$upscale"
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

if [ "$1" = "restore" ]; then

    rm -f "$upscale"/*.mp4

    mv "$temp"/*.mp4 "$upscale"
else
    mv "$upscale"/*.mp4 "$temp"

    run "intro.mp4"  iris-3
    run "intro2.mp4" iris-3
fi
