#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

ffmpeg="$PWD/../../../../../../Sky-runtime/bash/ffmpeg"

fix="$PWD/fix"

input="$PWD/.."

output="$PWD"

base="$PWD/base"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    if [ $# = 3 ]; then

        sh resize.sh "$fix/$1" "$2" "$output/$1" $3

    elif [ $# = 2 ]; then

        sh resize.sh "$fix/$1" "$input/$1" "$output/$1" $2
    else
        sh resize.sh "$fix/$1" "$input/$1" "$output/$1"
    fi
}

runBase()
{
    sh resize.sh "$fix/$1" "$base/$1" "$output/$1" $3 1
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

run "intro.mp4"    1
run "intro2.mp4"   1
run "college.mp4"  1
run "college2.mp4" 1

runBase "college3-1.mp4" 1
runBase "college3-2.mp4" 1
