#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

root="$PWD"

ffmpeg="$PWD/../../../../../../Sky/tools/ffmpeg"

fix="$PWD/fix"

input="$PWD/.."

output="$PWD"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    if [ $# = 2 ]; then

        sh resize.sh "$fix/$1" "$input/$1" "$output/$1" $2
    else
        sh resize.sh "$fix/$1" "$input/$1" "$output/$1"
    fi
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

run "college.mp4"
run "college2.mp4"
run "intro.mp4" 1
run "intro2.mp4"
