#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

root="$PWD"

ffmpeg="$PWD/../../../../../../Sky/tools/ffmpeg"

base="$PWD/base"

input="$PWD/.."

output="$PWD"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    sh resize.sh "$base/$1" "$input/$1" "$output/$1"
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

run "college.mp4"
run "college2.mp4"
run "intro.mp4"
run "intro2.mp4"
