#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

upscale()
{
    cd "$PWD/$1"/content

    sh upscale.sh

    cd -
}

render()
{
    cd "$PWD"/../..

    sh render.sh "$1"

    cd -
}

room()
{
    render "room/$1"
}

