#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

upscale()
{
    cd "$PWD/room/$1"/content

    sh upscale.sh

    cd -
}

renderBase()
{
    cd "$PWD"/..

    sh render.sh "$1"

    cd -
}

render()
{
    renderBase "room/$1"
}

#--------------------------------------------------------------------------------------------------
# Upscale
#--------------------------------------------------------------------------------------------------

#upscale "intro"
#upscale "attic"
upscale "attic2"
#upscale "chase"

#--------------------------------------------------------------------------------------------------
# Render
#--------------------------------------------------------------------------------------------------

#renderBase "movie"

render "intro"
render "attic"
render "attic2"
render "chase"
